import React from 'react';
import './Clock.css';
import './Clock.png';

const Clock = (day) => {
        return(
            <div class='clock'>
                <div class='hour'>
                    <div class='hr' id='hr' style={{transform:`rotateZ(${day.hh+(day.mm/12)}deg}`}}></div>
                </div>
                <div class='min'>
                    <div class='mn' id='mn' style={{transform:`rotateZ(${day.mm}deg)`}}></div>
                </div>
                <div class='sec'>
                    <div class='sc' id='sc' style={{transform:`rotateZ(${day.ss}deg)`}}></div>
                </div>
            </div>
        )
}
export default Clock;
