import React from 'react';
import './Clock.css';
import './Clock.png';

const Clock = (day) => {
        let deg = 6;
        let hh = day.getHours()*30;
        let mm = day.getMinutes()*deg;
        let ss = day.getSeconds()*deg;

        return(
            <div class='clock'>
                <div class='hour'>
                    <div class='hr' id='hr'></div>
                </div>
                <div class='min'>
                    <div class='mn' id='mn'></div>
                </div>
                <div class='sec'>
                    <div class='sc' id='sc'></div>
                </div>
            </div>
        )
}
export default Clock;
