import React, { Component } from 'react';
import Navi from './components/Navi/Navi';
import Textbar from './components/Textbar/Textbar';
import Textcontent from './components/Textcontent/Textcontent';
import './App.css';

const mapStateToProps = state => {
  return {route: state.RouteChange.route,
          status: state.SignedIn.status}
}

const mapDispatchToProps = (dispatch) => {
  return {
    onRouteChange:(text) => dispatch(RouteChange(text)),
    onSignedIn:(logtext) => dispatch(SignedIn(logtext))
  }
}

class App extends Component {

  render(){
    let route = window.location.pathname;
    let day = new Date();

    return (
      <div className="App">
        <div className="foxhole1"><Navi/></div>
        <div className="foxhole2"><Textbar/></div>
        <div className="foxhole3"><Textcontent route={route} day={day}/></div>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
