import React from 'react';
import './Clock.css';
import './Clock.png';

const Clock = () => {

        var day = new Date();
        let deg = 6;
        let hh = day.getHours();
        let mm = day.getMinutes()*deg;
        let ss = day.getSeconds()*deg;
        console.log(hh);

        return(
            <div class='clock'>
                <div class='hour'>
                    <div class='hr' id='hr' style={{transform:`rotateZ(${hh+(mm/12)}deg}`}}></div>
                </div>
                <div class='min'>
                    <div class='mn' id='mn' style={{transform:`rotateZ(${mm}deg)`}}></div>
                </div>
                <div class='sec'>
                    <div class='sc' id='sc' style={{transform:`rotateZ(${ss}deg)`}}></div>
                </div>
            </div>
        )
}
export default Clock;
